function spellNumberSrb(number, prefiks = '') {
    let reci = `${number}`;

    reci = prefiks + reci;

    return reci;
}

function makeTimeStringSrb(time) {
    let hours = time.getHours() % 12;
    hours = hours ? hours : 12; // hour '0' should be '12'
    let minutes = time.getMinutes();
    let ampm = time.getHours() >= 12 ? 'pm' : 'am';

    let recenica = 'sada je ';
    
    minutes -= minutes % 5;

    if (minutes == 0) {
        recenica += 'pola ';
        recenica += spellNumberSrb(hours, 'c');
        if (hours == 1) recenica += ' sat';
        else if (hours < 5) recenica += ' sata';
        else recenica += ' sati';
    } else if (minutes <= 30) {

        recenica += spellNumberSrb(minutes, 's');
        recenica += ' i ';
        recenica += spellNumberSrb(hours, 'c');

    } else {
        minutes = 60 - minutes;
        hours++;
        hours = hours > 12 ? hours - 12 : hours; 
        recenica += spellNumberSrb(minutes, 'p');
        recenica += ' do ';
        recenica += spellNumberSrb(hours, 'c');
    }

    return recenica;
}

function updateTimeString(timeString) {
    let reci = makeTimeStringSrb(timeString);
    let reciZaPrikaz = reci.split(' ');

    reciZaPrikaz.forEach(rec => {
        // document.getElementById(rec).style.color = 'black';
        // document.getElementById(rec).style.fontWeight = '900';
        for (const el of [...document.querySelectorAll(`.${rec}`)]) {
            el.style.color = 'black';
            el.style.fontWeight = '900';
        }
        
    })

}

let time = new Date();

updateTimeString(time);